import React, {useState} from "react";

import Switch from "react-bootstrap-switch";


// reactstrap components
import {
  Button,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import LandingPageHeader from "components/Headers/LandingPageHeader.js";
import DefaultFooter from "components/Footers/DefaultFooter.js";
import IndexNavbar from "components/Navbars/IndexNavbar.js";
import Editor from "components/Editor.js";

const par = {float: "left"};


function LandingPage() {
  const [firstFocus, setFirstFocus] = React.useState(false);
  const [lastFocus, setLastFocus] = React.useState(false);
  const [disabled, setDisabled] = useState(false);

  function handleTogg() {
    setDisabled(!disabled);
  }
  React.useEffect(() => {
    document.body.classList.add("landing-page");
    document.body.classList.add("sidebar-collapse");
    document.documentElement.classList.remove("nav-open");
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;
    return function cleanup() {
      document.body.classList.remove("landing-page");
      document.body.classList.remove("sidebar-collapse");
    };

  }, []);

  return (
    <>
      <IndexNavbar />
      <div className="wrapper">
        <LandingPageHeader />
        <div className="section section-contact-us text-center">
          <Container>
            <h2 className="title">Tulis Disini?</h2>
            <p className="description">Your story is very important to us.</p>
            <Row>
            <Col className="ml-auto mr-auto" lg="15" md="8">
            <p className="text-left category">Tulis Sebagai Anonim</p> <span className=""> <Switch 
              checked={false}
              name='test' 
              offColor="" 
              offText="" 
              onColor="" 
              onText=""
              style={par}
              onChange={handleTogg}
              ></Switch></span>
            </Col>
            </Row>
            <Row>
              <Col className="text-center ml-auto mr-auto" lg="15" md="8">
                <InputGroup
                  className={
                    "input-lg" + (firstFocus ? " input-group-focus" : "")
                  }
                >
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="now-ui-icons users_circle-08"></i>
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input
                    placeholder="Full Name..."
                    type="text"
                    onFocus={() => setFirstFocus(true)}
                    onBlur={() => setFirstFocus(false)}
                    id = "nama"
                    disabled={disabled}
                  ></Input>
                </InputGroup>
                <InputGroup
                  className={
                    "input-lg" + (lastFocus ? " input-group-focus" : "")
                  }
                >
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="now-ui-icons ui-1_email-85"></i>
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input
                    placeholder="Email... (Optional)"
                    type="text"
                    onFocus={() => setLastFocus(true)}
                    onBlur={() => setLastFocus(false)}
                    id = "email"
                    disabled={disabled}
                  ></Input>
                </InputGroup>
                <div className="textarea-container">
                  <Editor />
                </div>
                <div className="send-button">
                  <Button
                    block
                    className="btn-round"
                    color="info"
                    href="#pablo"
                    onClick={(e) => e.preventDefault()}
                    size="lg"
                  >
                    Send Message
                  </Button>
                </div>
              </Col>
            </Row>
            {/* <Row>
              <Col lg="9" md="12">
                <Editor />
              </Col>
            </Row> */}
          </Container>
        </div>
        <DefaultFooter />
      </div>
    </>
  );
}

export default LandingPage;
