import React,{useState} from "react";
import ReactQuill from "react-quill";
import { Card, CardBody, Form, FormInput } from "shards-react";

import "react-quill/dist/quill.snow.css";
import "../assets/quill.css";


const Editor = (props,popp) => {
  console.log(props)
  const initialState = {
    title: "",
    story: "",
  };

  //initialise our state
  const [ourState, ourSetState] = useState(initialState);

  const { name, address, occupation } = ourState;

  return(
  <Card small className="mb-3">
    <CardBody>
      <Form className="add-new-post">
        <FormInput size="lg" className="mb-3" placeholder="Judul .." required="required" 
        onChange = {(event) => props.onChange(event.target.value)}
        />
        <ReactQuill 
        className="add-new-post__editor mb-1" 
        placeholder="Tulis disini..." 
        required="required"
        onChange = {(ev) => popp.onChange(ev.target.value)}
        />
      </Form>
    </CardBody>
  </Card>
)};

export default Editor;
