import React,{useState} from "react";
import ReactQuill from "react-quill";
import { Card, CardBody, Form, FormInput } from "shards-react";

import "react-quill/dist/quill.snow.css";
import "../assets/quill.css";


const Editor = (props) => {
  console.log(props)
  const [judul, setjudul] = useState('');
  const [isi, setisi] = useState('');


  //initialise our state
  const [ourState, ourSetState] = useState(initialState);

  const { title, story } = ourState;

  function handleEventChange(event) {
    ourSetState({ ourState, [event.target.name]: event.target.value });
  }

  return(
  <Card small className="mb-3">
    <CardBody>
      <Form className="add-new-post">
        <FormInput size="lg" className="mb-3" placeholder="Judul .." required="required" 
        onChange = {handleEventChange} value={title} name="title"
        />
        <ReactQuill 
        className="add-new-post__editor mb-1" 
        placeholder="Tulis disini..." 
        required="required"
        onChange = {handleEventChange}
        value={story}
        name= "story"
        />
      </Form>
    </CardBody>
  </Card>
)};

export default Editor;
