import React from "react";
import ReactDOM from 'react-dom';
import WordLimit from 'react-word-limit';


// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Container,
  Row,
  Col,

  Button,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,

  Pagination,
  PaginationItem,
  PaginationLink,

} from "reactstrap";

// core components

const par = {margintop: "0px", marginbottom: "1rem", height: "215px", overflow: "hidden"};

const LimitedWordTextarea = ({ rows, cols, value, limit }) => {
  const [content, setContent] = React.useState(value);
  const [wordCount, setWordCount] = React.useState(0);

  const setFormattedContent = text => {
    let words = text.split(' ');
    if (words.filter(Boolean).length > limit) {
      setContent(
        text
          .split(' ')
          .slice(0, limit)
          .join(' ')
      );
      setWordCount(limit);
    } else {
      setContent(text);
      setWordCount(words.filter(Boolean).length);
    }
  };

  React.useEffect(() => {
    setFormattedContent(content);
  }, []);

  return (
    <div>
      <textarea
        rows={rows}
        cols={cols}
        onChange={event => setFormattedContent(event.target.value)}
        value={content}
      />
      <p>
        {wordCount}/{limit}
      </p>
    </div>
  );
};


function Tabs() {
  const [iconPills, setIconPills] = React.useState("1");
  const [pills, setPills] = React.useState("1");
  return (
    <>
      <div className="section section-tabs"
      id="baca-section">
        <Container>
          <Row className="ml-auto mr-auto" md="10" xl="25">
              <p className="category">Carita.</p>
              <Card>
                <CardHeader>
                  <Nav className="justify-content-center">
                    <NavItem>
                      <NavLink
                        className={iconPills === "1" ? "active" : ""}
                        href="#pablo"
                        onClick={(e) => {
                          e.preventDefault();
                          setIconPills("1");
                        }}
                      >
                       <h1 className="category">Hahahahahahahahaha</h1> 
                      </NavLink>
                    </NavItem>
                  </Nav>
                </CardHeader>
                <CardBody>
                  <TabContent
                    className="text-justify"
                    activeTab={"iconPills" + iconPills}
                  >
                    <TabPane  style={par} tabId="iconPills1">
                      <p>
                      <LimitedWordTextarea limit={5} value="Hello there!" />
                      </p>
                    </TabPane>
                  </TabContent>
                </CardBody>
              </Card>
            </Row>
        </Container>
      </div>

    </>
  );
}

export default Tabs;
