import React,{useState} from "react";
import ReactQuill from "react-quill";
import Quill from "quill";
import { Card, CardBody, Form, FormInput } from "shards-react";

import "react-quill/dist/quill.snow.css";
import "../assets/quill.css";


const Editor = () => {
  const [vall, setVall] = React.useState({judl: '', isi: ''});
  const quillEditor = {height: "180px", overflow: "auto"};

  // sto ={
  //   judl: '',
  //   isi: ''
  // };
  const handleChange = name => event => {
    setVall({...vall, [name]: event.target.value});
  }
  console.log(vall.judl)

  var options = {
    placeholder: 'Waiting for your precious content',
    theme: 'snow'
  };

  var editor = new Quill('#quillEditor', options);

  return(
  <Card small className="mb-3">
    <CardBody>
      <Form className="add-new-post">
        <FormInput size="lg" className="mb-3" placeholder="Judul .." required="required" 
        name="judl"
        onChange = {handleChange('judl')} 
        value={vall.judl} 
        />
        {/* <ReactQuill 
        className="add-new-post__editor mb-1" 
        placeholder="Tulis disini..." 
        required="required"
        name="isi"
        onChange = {handleChange('isi')} 
        value={vall.isi} 
        /> */}
        <div id="quillEditor" style={quillEditor}>
        </div>
      </Form>
    </CardBody>
  </Card>
)};

export default Editor;
